const greeting = 'Hello, world!'
const vowels = ['a', 'e', 'i', 'o', 'u']
let count = 0

for (let vowel of vowels) {
  const newGreeting = greeting.toLowerCase()
  for (let item of newGreeting) {
    if(item === vowel) {
      count += 1
    }
  }
}
console.log(count)

